import React from 'react';
import ReactDOM from 'react-dom';
class Task1 extends React.Component{
    state = {
        yStart:'',
        yEnd:'',
        isSubmitted: false
      }
    render(){
        const ques="Write a function that takes year range as a parameter and prints a timeseries or bar graph showing titles published in each year for the given range.";

        return (
        <div className="task">
            <div className="ques">
                {ques}
            </div>
            <div className="quesBody">
                <form onSubmit={this.onSubmit.bind(this)}>
                <label for="fname">Year Start:</label>
                    <input type="text" id="fname" name="fname" value={this.state.yStart}/><br/><br/>
                    <label for="lname">Year End:</label>
                    <input type="text" id="lname" name="lname" value={this.state.yEnd}/><br/><br/>
                    <input type="submit" value="Submit"/>
                    <input type="reset"/>
                </form>
                {this.state.isSubmitted && fetchSolution}
            </div>
        </div>);
    }
    onSubmit(event){
        event.preventDefault();
        this.setState({isSubmitted:true})
        const formData = {
            yearStart: this.state.yStart,
            yearEnd: this.state.yEnd
        };

    }

}
function fetchSolution(){
    return (
        <h1>This is your solition</h1>
    );
}
