import React from 'react';
import ReactDOM from 'react-dom';
class Task extends React.Component{
    fetchTask(i){
        console.log(i);

        let ques="";
        if(i===1){
            ques=this.Task1();
        }
        return ques;
    }
    Task1(){
        const ques="Write a function that takes year range as a parameter and prints a timeseries or bar graph showing titles published in each year for the given range.";
        const body="";
        return [ques,body];
    }
    render(){
        let taskId=this.props.taskId;
        let task=this.fetchTask(taskId);
        return (<div className="task">
            <div className="ques">
                {task[0]}
            </div>
            <div className="quesBody">
                {task[1]}
            </div>
        </div>);
    }
}
ReactDOM.render(<Task />,document.getElementById("root"));
export default Task;
