import React from 'react';
import ReactDOM from 'react-dom';
class Task1 extends React.Component{
    state = {
        isSubmitted: false
      }
    render(){
        const ques="Write a function that takes year range as a parameter and prints a timeseries or bar graph showing titles published in each year for the given range.";

        return (
        <div className="task">
            <div className="ques">
                {ques}
            </div>
            <div className="quesBody">
                <form onSubmit={this.fetchSolution}>
                <label for="fname">Year Start:</label>
                    <input type="text" id="fname" name="fname"/><br/><br/>
                    <label for="lname">Year End:</label>
                    <input type="text" id="lname" name="lname"/><br/><br/>
                    <input type="submit" value="Submit"/>
                    <input type="reset"/>
                </form>
            </div>
        </div>);
    }
    onSubmit(event){
        this.setState({isSubmitted:true})
    }
    fetchSolution(){
        return (
            <h1>This is your solition</h1>
        );
    }
}
// ReactDOM.render(<Task1 />,document.getElementById("root"));
export default Task1;
