import React from 'react';
import {Bar,Chart} from 'react-chartjs-2';
class Task1 extends React.Component{
    constructor(props){
        super(props);
        this.state = {
        yStart:null,
        yEnd:null,
        isSubmitted: false
      };
      this.onChange = this.onChange.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
    }
    render(){
        const ques="Write a function that takes year range as a parameter and prints a timeseries or bar graph showing titles published in each year for the given range.";

        return (
        <div className="task">
            <div className="ques">
                {ques}
            </div>
            <div className="quesBody">
                <form onSubmit={this.onSubmit}>
                <label>Year Start:</label>
                    <input type="text" id="yStart" name="yStart" value={this.state.yStart} onChange={this.onChange}/><br/><br/>
                    <label>Year End:</label>
                    <input type="text" id="yEnd" name="yEnd" value={this.state.yEnd} onChange={this.onChange}/><br/><br/>
                    <input type="submit" value="Submit" disabled={this.state.isSubmitted}/>
                </form>

                {this.state.isSubmitted &&
                <Solution yStart={this.state.yStart} yEnd={this.state.yEnd}/>}
            </div>
        </div>);
    }
    onSubmit(event){
        event.preventDefault();
        this.setState({isSubmitted:true})
        // const formData = {
        //     yStart: this.state.yStart,
        //     yEnd: this.state.yEnd
        // };
        // console.log(formData);
    }
    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });  // Getting access to entered values
        this.setState({isSubmitted:false})
     }

}



class Solution extends React.Component{
    chartReference = React.createRef();
    constructor(props){
        super(props);
        this.state={
            yStart:null,
            yEnd:null,
            dataPoints:null,
            isLoaded:false
        }
    }

    componentDidMount() {
        const apiUrl = 'http://127.0.0.1:8000/task1/';
        var label=[]
        var data=[]
        fetch(apiUrl).then((response) => response.json()).then((jsonData) =>(
            jsonData.map((singleData)=>(
                label.push(singleData.year),
                data.push(singleData.count)
            ))
        ));

        console.log(label)

        const stateData = {
            labels: label,
            datasets: [
              {
                label: 'Tiltles Published Per Year',
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                data: data
              }
            ]
          }
        this.setState( {dataPoints:stateData

            })
             const myChartRef = this.chartReference.current.getContext("2d");
             new Chart(myChartRef, {
                type: "line",
                data: {
                    //Bring in data
                    labels: label,
                    datasets: [
                        {
                            label:'Tiltles Published Per Year',
                            data: data,
                        }
                    ]
                },
                options: {
                    //Customize chart options
                }
            });

    }

    render(){
        // if(!this.state.isLoaded) {
        //     return <div>loading ...</div>;
        // }
       // this.state.

        // const options = {
		// 	title: {
		// 		text: "Titles Published in Each Year"
		// 	},
		// 	data: [
		// 	{
		// 		// Change type to "doughnut", "line", "splineArea", etc.
		// 		type: "column",
		// 		dataPoints: this.state.dataPoints
		// 	}
		// 	]
        // }
        // console.log(options)
        // // console.log(this.state.dataPoints)
        // const data=[
        //     {
        //       "name": "A",
        //       "value": 46
        //     },
        //     {
        //       "name": "B",
        //       "value": 87
        //     }
        //   ]
        return  (
            <div>
                <canvas id="barCanvas" ref={this.chartReference}/>
                <Bar data={this.state.dataPoints}/>
                <BarChart/>
                </div>


        );
    }
}
